
const texto=document.getElementById("textohover")

const asigntext=(event)=>{
const textshow={
    1: " ",
    2: " xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx",
    3: " yyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyy"
} 
const p=document.createElement("p")

p.textContent+=textshow[event]
texto.appendChild(p)
}

const removetext=()=>{
    texto.innerHTML=""
}

